function formatNumber($number) {\n    // Pad the number to ensure it is 9 digits long\n    $paddedNumber = str_pad($number, 9, '0', STR_PAD_LEFT);\n    \n    // Use a regular expression to insert slashes\n    $formattedNumber = preg_replace('/(\d{3})(\d{3})(\d{3})/', '$1/$2/$3', $paddedNumber);\n    \n    return $formattedNumber;\n}\n\n// Examples\necho formatNumber(12345);    // Outputs: 000/012/345\necho formatNumber(9876543);  // Outputs: 009/876/543