// ExampleForm.php\n   class ExampleForm extends Zend_Form {\n       public function __construct($options = null) {\n           parent::__construct($options);\n           $this->setMethod('post');\n           $this->addElement('text', 'exampleField', array(\n               'label' => 'Example Field',\n               'required' => true,\n           ));\n           $this->addElement('submit', 'edit', array('label' => 'Edit'));\n           $this->addElement('submit', 'delete', array('label' => 'Delete'));\n       }\n\n       public function processSubmit() {\n           if ($this->isValid($_POST)) {\n               if ($this->getElement('edit')->isChecked()) {\n                   // Process edit action\n               } elseif ($this->getElement('delete')->isChecked()) {\n                   // Process delete action\n               }\n           }\n       }\n   }