<?php\n// Function to load words from a file and cache them\nfunction loadWords() {\n    // Check if words are already cached\n    $words = apcu_fetch('captcha_words');\n    if ($words === false) {\n        // Read words from file\n        $words = file('words.txt', FILE_IGNORE_NEW_LINES | FILE_SKIP_EMPTY_LINES);\n        // Cache words in APCu\n        apcu_store('captcha_words', $words);\n    }\n    return $words;\n}\n\n// Function to get a random word from the list\nfunction getRandomWord() {\n    $words = loadWords();\n    $randomIndex = array_rand($words);\n    return $words[$randomIndex];\n}\n\n// Example usage\n$captchaWord = getRandomWord();\necho "Random Captcha Word: " . htmlspecialchars($captchaWord);