<?php\n$url = 'http://example.com/path/to/your/xmlfile.xml';\n$savePath = '/path/to/save/xmlfile.xml';\n\n// Initialize a cURL session\n$ch = curl_init($url);\n\n// Open a file in write mode to save the downloaded content\n$fp = fopen($savePath, 'w');\n\nif ($fp === false) {\n    die('Error: Unable to open file for writing.');\n}\n\n// Set cURL options\ncurl_setopt($ch, CURLOPT_FILE, $fp);\ncurl_setopt($ch, CURLOPT_TIMEOUT, 20); // Timeout in seconds\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true); // Follow redirects if necessary\n\n// Execute the cURL session\n$result = curl_exec($ch);\n\n// Check for errors\nif ($result === false) {\n    $error = curl_error($ch);\n    curl_close($ch);\n    fclose($fp);\n    die('cURL error: ' . $error);\n}\n\n// Close the cURL session and file\ncurl_close($ch);\nfclose($fp);\n\necho 'File downloaded successfully.';\n?>