function get_http_status_code($url) {\n    $headers = @get_headers($url);\n    if ($headers && isset($headers[0])) {\n        // Extract the HTTP status code from the first header\n        $status_line = $headers[0];\n        preg_match('{HTTP\/\S*\s(\d{3})}', $status_line, $match);\n        return isset($match[1]) ? (int)$match[1] : false;\n    }\n    return false;\n}\n\n// Example usage:\n$url = "http://example.com";\n$status_code = get_http_status_code($url);\necho "HTTP status code for $url is: " . $status_code;