<?php\n// Database connection as above\n// ...\n\nif ($_SERVER['REQUEST_METHOD'] === 'GET') {\n    $email = htmlspecialchars($_GET['email']);\n    $token = htmlspecialchars($_GET['token']);\n\n    // Verify token and update status\n    $stmt = $pdo->prepare("SELECT * FROM petition_signatures WHERE email = ? AND token = ? AND status = 'pending'");\n    $stmt->execute([$email, $token]);\n    $signature = $stmt->fetch();\n\n    if ($signature) {\n        // Update status to confirmed\n        $stmt = $pdo->prepare("UPDATE petition_signatures SET status = 'confirmed' WHERE email = ? AND token = ?");\n        $stmt->execute([$email, $token]);\n\n        // Send petition email to target\n        $petitionEmail = "target@example.com";\n        $subject = "New Petition Signature";\n        $message = "A new signature has been added to the petition:\n\n";\n        $message .= "Name: {$signature['name']}\n";\n        $message .= "Email: {$signature['email']}\n";\n        $headers = "From: no-reply@yourdomain.com";\n\n        mail($petitionEmail, $subject, $message, $headers);\n\n        echo "Your signature has been confirmed and the petition email has been sent.";\n    } else {\n        echo "Invalid confirmation link or already confirmed.";\n    }\n}\n?>