<?php\nsession_start();\nif ($_SERVER["REQUEST_METHOD"] == "POST") {\n    // Use appropriate methods to sanitize user input\n    $username = htmlspecialchars($_POST['username']);\n    $password = htmlspecialchars($_POST['password']); // Consider using hashing for passwords\n\n    // Store the sanitized input in session variables\n    $_SESSION['username'] = $username;\n    $_SESSION['password'] = $password;\n\n    // Redirect to another page where you want to use these variables\n    header("Location: welcome.php");\n    exit();\n}\n?>