function hexToStr($hex) {\n    $str = '';\n    for ($i = 0; $i < strlen($hex); $i += 2) {\n        $str .= chr(hexdec(substr($hex, $i, 2)));\n    }\n    return $str;\n}\n\nfunction convertUtf16ToUtf8($utf16HexStr) {\n    // Remove the \00 from the hex string\n    $utf16HexStr = str_replace('\00', '', $utf16HexStr);\n\n    // Convert hexadecimal string to binary string\n    $binaryStr = hexToStr($utf16HexStr);\n\n    // Convert binary string from UTF-16 to UTF-8\n    $utf8Str = mb_convert_encoding($binaryStr, 'UTF-8', 'UTF-16');\n\n    return $utf8Str;\n}\n\n$utf16HexStr = "R\00f6dhakev\00e4gen 4\nR\00e4ntm\00e4starv\00e4gen 24\nK\00d8BENHAVN\n";\n$utf8Str = convertUtf16ToUtf8($utf16HexStr);\n\necho $utf8Str;