// Using mysqli with persistent connections\n$host = 'localhost';\n$username = 'your_username';\n$password = 'your_password';\n$database = 'your_database';\n\n// Note the "p:" prefix in the hostname for persistent connection\n$mysqli = new mysqli('p:'.$host, $username, $password, $database);\n\nif ($mysqli->connect_error) {\n    die('Connect Error (' . $mysqli->connect_errno . ') ' . $mysqli->connect_error);\n}\n\n// Use $mysqli for your database operations\n\n$mysqli->close(); // Close the connection when done (optional for persistent)