// Clean a specific GET variable\n$clean_get_var = filter_input(INPUT_GET, 'variable_name', FILTER_SANITIZE_STRING);\n\n// Clean a specific POST variable\n$clean_post_var = filter_input(INPUT_POST, 'variable_name', FILTER_SANITIZE_STRING);\n\n// Example of sanitizing all GET variables\nforeach ($_GET as $key => $value) {\n    $_GET[$key] = filter_var($value, FILTER_SANITIZE_STRING);\n}\n\n// Example of sanitizing all POST variables\nforeach ($_POST as $key => $value) {\n    $_POST[$key] = filter_var($value, FILTER_SANITIZE_STRING);\n}