<?php\nfunction parseCSS($css) {\n    $result = [];\n    preg_match_all('/([^{]+){([^}]+)}/', $css, $matches, PREG_SET_ORDER);\n    foreach ($matches as $match) {\n        $selector = trim($match[1]);\n        $properties = explode(';', trim($match[2]));\n        foreach ($properties as $property) {\n            if (trim($property)) {\n                list($name, $value) = explode(':', $property);\n                $result[$selector][] = [\n                    trim($name) => trim($value)\n                ];\n            }\n        }\n    }\n    return $result;\n}\n\n// Example usage\n$css = "#selector{ display:block; width:100px; }\n#selector a{ float:left; text-decoration:none; }";\n$parsedCSS = parseCSS($css);\nprint_r($parsedCSS);\n?>