<?php\n// Example PHP script to set dynamic ports in configuration files\n$apacheConf = 'path/to/xampp/apache/conf/httpd.conf';\n$mysqlConf = 'path/to/xampp/mysql/bin/my.ini';\n\n$apachePort = findAvailablePort();\n$mysqlPort = findAvailablePort();\n\nupdateConfigFile($apacheConf, 'Listen 80', "Listen $apachePort");\nupdateConfigFile($mysqlConf, 'port=3306', "port=$mysqlPort");\n\nfunction findAvailablePort() {\n    $port = 8000; // Starting port\n    while (isPortInUse($port)) {\n        $port++;\n    }\n    return $port;\n}\n\nfunction isPortInUse($port) {\n    $connection = @fsockopen('localhost', $port);\n    if ($connection) {\n        fclose($connection);\n        return true;\n    }\n    return false;\n}\n\nfunction updateConfigFile($filePath, $search, $replace) {\n    $config = file_get_contents($filePath);\n    $config = str_replace($search, $replace, $config);\n    file_put_contents($filePath, $config);\n}\n?>