<?php\n// Assume $_FILES['uploaded_file'] contains the uploaded file information\n$uploadedFile = $_FILES['uploaded_file']['tmp_name'];\n$finalPath = 'C:\\path\\to\\final\\location\\' . $_FILES['uploaded_file']['name'];\n\n// Move the uploaded file to the final location\nif (move_uploaded_file($uploadedFile, $finalPath)) {\n    // Set the desired permissions using icacls\n    $command = 'icacls ' . escapeshellarg($finalPath) . ' /grant "Domain Users:(R)" /T /C';\n    exec($command, $output, $returnVar);\n    \n    if ($returnVar === 0) {\n        echo "File uploaded and permissions set successfully!";\n    } else {\n        echo "Failed to set permissions. Error: " . implode("\n", $output);\n    }\n} else {\n    echo "Failed to upload file.";\n}\n?>