<?php\n// Define login URL and the URL of the page you want to access\n$loginUrl = "http://remoteServer/login.php?user=xxx&pass=yyy";\n$targetUrl = "http://remoteServer/pageicareabout.html";\n\n// Define the cookie file\n$cookieFile = "/path/to/cookie.txt"; // Make sure this file is writable\n\n// Step 1: Log in and save the cookie\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $loginUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEJAR, $cookieFile); // Save cookies to the file\ncurl_setopt($ch, CURLOPT_COOKIEFILE, $cookieFile); // Use cookies from the file\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // If HTTPS, disable SSL verification\ncurl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);\ncurl_exec($ch);\ncurl_close($ch);\n\n// Step 2: Access the desired page with the saved cookie\n$ch = curl_init();\ncurl_setopt($ch, CURLOPT_URL, $targetUrl);\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, true);\ncurl_setopt($ch, CURLOPT_COOKIEFILE, $cookieFile); // Use cookies from the file\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, true);\ncurl_setopt($ch, CURLOPT_SSL_VERIFYPEER, false); // If HTTPS, disable SSL verification\ncurl_setopt($ch, CURLOPT_SSL_VERIFYHOST, false);\n$response = curl_exec($ch);\ncurl_close($ch);\n\n// Output the response\necho $response;\n?>