// Connect to MySQL database\n$mysqli = new mysqli("hostname", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Query to get the count of visits per hour in the last 24 hours\n$sql = "\nSELECT \n    HOUR(added) AS hour,\n    COUNT(*) AS visits\nFROM \n    visits\nWHERE \n    added >= NOW() - INTERVAL 1 DAY\nGROUP BY \n    HOUR(added)\nORDER BY \n    HOUR(added);\n";\n\n// Execute the query\n$result = $mysqli->query($sql);\n\n// Check if query was successful\nif (!$result) {\n    die("Query failed: " . $mysqli->error);\n}\n\n// Fetch the results\n$visits_per_hour = [];\nwhile ($row = $result->fetch_assoc()) {\n    $visits_per_hour[$row['hour']] = $row['visits'];\n}\n\n// Close the connection\n$mysqli->close();\n\n// Output the visits per hour\nprint_r($visits_per_hour);