<?php\n$body = file_get_contents('index.htm');\n\n// Define the regular expression pattern to match the <td><table>...</table></td> section\n$pattern = '/<td><table>.*?<\/table><\/td>/s';\n\n// Use preg_replace to remove the matched section\n$body = preg_replace($pattern, '', $body);\n\n// Save the modified content back to the file if needed\nfile_put_contents('index_modified.htm', $body);\n\necho $body;\n?>