// $connection is a resource\nfunction DoSomething1(&$connection) {\n    // Perform database operations using the original connection\n}\n\nfunction DoSomething2($connection) {\n    // Perform database operations using a copy of the connection\n}\n\n// Create a MySQL connection\n$connection = mysqli_connect('localhost', 'user', 'password', 'database');\n\n// Pass by reference\nDoSomething1($connection);\n\n// Pass by value\nDoSomething2($connection);