// In your theme's functions.php or a custom plugin\n\nadd_action('wpcfm_register_bundles', function() {\n    $bundle = array(\n        'label' => 'Content Sync Bundle',\n        'config' => array(\n            'posts' => array(\n                'post_type' => 'post',\n                'post_status' => 'publish',\n            ),\n            'taxonomies' => array(\n                'category' => array(\n                    'taxonomy' => 'category',\n                    'terms' => array(\n                        'slug' => 'your-category-slug',\n                    ),\n                ),\n            ),\n        ),\n    );\n\n    wpcfm()->bundles->register('content_sync_bundle', $bundle);\n});