$weights = array(0.25, 0.4, 0.2, 0.15);\n$values  = array(\n    array(5, 10, 15),\n    array(20, 25, 30),\n    array(35, 40, 45),\n    array(50, 55, 60)\n);\n\n$result = array_map(function($colIndex) use ($values, $weights) {\n    return array_reduce(array_keys($weights), function($carry, $rowIndex) use ($values, $weights, $colIndex) {\n        return $carry + $weights[$rowIndex] * $values[$rowIndex][$colIndex];\n    }, 0);\n}, array_keys($values[0]));\n\nprint_r($result);