function IntegerOverflowAdd($a, $b) {\n    $sum = $a + $b;\n    return $sum & 0xFFFFFFFF;\n}\n\nfunction f($x1, $x2, $x3, $x4) {\n    $sum = IntegerOverflowAdd($x1, $x2);\n    $xor = $sum ^ $x3;\n    $result = IntegerOverflowAdd($xor, $x4);\n    return $result > 0x7FFFFFFF ? $result - 0x100000000 : $result;\n}\n\n// Example usage:\n$x1 = -1580033017;\n$x2 = -2072974554;\n$x3 = -1170476976;\n$x4 = -1007518822;\n\necho f($x1, $x2, $x3, $x4); // Output should match C# result