<?php\n$file = 'path/to/your/logfile.txt';\n$linesToRemove = 5; // Number of lines to remove from line 3 onwards\n$maxFileSize = 1024 * 1024; // Example max file size in bytes (1MB)\n\nif (file_exists($file) && filesize($file) > $maxFileSize) {\n    $fileContent = file($file, FILE_IGNORE_NEW_LINES); // Read file into array\n\n    if (count($fileContent) > 2) {\n        // Remove lines starting from index 2\n        $fileContent = array_merge(\n            array_slice($fileContent, 0, 2), // Keep first two lines\n            array_slice($fileContent, 2 + $linesToRemove) // Remove specified number of lines\n        );\n\n        // Write modified content back to file\n        file_put_contents($file, implode(PHP_EOL, $fileContent) . PHP_EOL);\n    }\n}\n?>