function clean($str) {\n    // Read bad words from file\n    $handle = fopen("badwords.txt", "r");\n    $array = array();\n    if ($handle) {\n        while (($line = fgets($handle)) !== false) {\n            $array[] = trim($line); // Trim whitespace including newline characters\n        }\n        fclose($handle);\n    }\n\n    $filter = "[explicit]";\n    $clean = str_replace($array, $filter, $str);\n    return $clean;\n}\n\n// Example usage\n$input = "This is a badword example";\necho clean($input);