function stripPTagClasses($html) {\n    // Regex to find <p> tags with classes and replace them with <p> tags without classes\n    $pattern = '/<p\s+class="[^"]*">/';\n    $replacement = '<p>';\n    return preg_replace($pattern, $replacement, $html);\n}\n\n$html = '<p class="example">This is a paragraph.</p><p class="another-class">Another paragraph.</p>';\n$cleanHtml = stripPTagClasses($html);\n\necho $cleanHtml; // Output: <p>This is a paragraph.</p><p>Another paragraph.</p>