<?php\n$hostname = '{imap.example.com:993/imap/ssl}INBOX';\n$username = 'your-email@example.com';\n$password = 'your-email-password';\n\n// Try to connect to the IMAP server\n$inbox = imap_open($hostname, $username, $password) or die('Cannot connect to IMAP server: ' . imap_last_error());\n\n// Search for emails\n$emails = imap_search($inbox, 'UNSEEN');\n\nif ($emails) {\n    foreach ($emails as $email_number) {\n        $overview = imap_fetch_overview($inbox, $email_number, 0);\n        $message = imap_fetchbody($inbox, $email_number, 1);\n\n        // Process the email contents and save it to the database\n        saveEmailToDatabase($overview[0]->subject, $message);\n        \n        // Mark the email as seen\n        imap_setflag_full($inbox, $email_number, "\\Seen");\n    }\n}\n\n// Close the connection\nimap_close($inbox);\n\nfunction saveEmailToDatabase($subject, $body) {\n    $servername = "your-database-host";\n    $username = "your-database-username";\n    $password = "your-database-password";\n    $dbname = "your-database-name";\n\n    // Create connection\n    $conn = new mysqli($servername, $username, $password, $dbname);\n\n    // Check connection\n    if ($conn->connect_error) {\n        die("Connection failed: " . $conn->connect_error);\n    }\n\n    // Prepare and bind\n    $stmt = $conn->prepare("INSERT INTO emails (subject, body) VALUES (?, ?)");\n    $stmt->bind_param("ss", $subject, $body);\n\n    // Execute the statement\n    $stmt->execute();\n    $stmt->close();\n    $conn->close();\n}\n?>