// Using bindParam\n$stmt = $pdo->prepare('SELECT * FROM users WHERE id = :id');\n$id = 1;\n$stmt->bindParam(':id', $id, PDO::PARAM_INT);\n$id = 2; // Changing the value before execution\n$stmt->execute();\n// The query will use id = 2\n\n// Using bindValue\n$stmt = $pdo->prepare('SELECT * FROM users WHERE id = :id');\n$id = 1;\n$stmt->bindValue(':id', $id, PDO::PARAM_INT);\n$id = 2; // Changing the value before execution\n$stmt->execute();\n// The query will use id = 1