<?php\n// Database connection\n$servername = "your_server";\n$username = "your_username";\n$password = "your_password";\n$dbname = "your_database";\n\n$conn = new mysqli($servername, $username, $password, $dbname);\n\n// Check connection\nif ($conn->connect_error) {\n    die("Connection failed: " . $conn->connect_error);\n}\n\n// Function to increment hit count\nfunction incrementHitCount($conn, $page_id) {\n    $date = date('Y-m-d');\n    $sql = "INSERT INTO hits (id, date, hits) VALUES ($page_id, '$date', 1)\n            ON DUPLICATE KEY UPDATE hits = hits + 1";\n    $conn->query($sql);\n}\n\n$page_id = 1; // Example page/item id\n$cookie_name = "visited_$page_id";\n$cookie_value = "true";\n$cookie_lifetime = 86400; // 1 day in seconds\n\n// Check if the cookie is set\nif(!isset($_COOKIE[$cookie_name])) {\n    // Cookie not set, increment the hit count\n    incrementHitCount($conn, $page_id);\n    \n    // Set the cookie\n    setcookie($cookie_name, $cookie_value, time() + $cookie_lifetime, "/");\n}\n\n// Close the database connection\n$conn->close();\n?>