// Load your XML or HTML document\n$doc = new DOMDocument();\n$doc->loadXML('<root><element attr1="value1" attr2="value2" /></root>');\n\n// Get the element\n$element = $doc->getElementsByTagName('element')->item(0);\n\n// Check if the element is valid and is an instance of DOMElement\nif($element instanceof DOMElement) {\n    // Remove the attribute 'attr1'\n    $element->removeAttribute('attr1');\n}\n\n// Save and output the modified XML\necho $doc->saveXML();