<?php\nfunction changePermissions($command, $target, $owner = '', $permissions = '') {\n    // Validate and sanitize inputs\n    $validCommands = ['chown', 'chmod'];\n    if (!in_array($command, $validCommands)) {\n        throw new Exception('Invalid command');\n    }\n\n    $target = escapeshellarg($target);\n    $owner = escapeshellarg($owner);\n    $permissions = escapeshellarg($permissions);\n\n    $cmd = "sudo /path/to/change_permissions.sh $command $target $owner $permissions";\n    $output = shell_exec($cmd);\n    return $output;\n}\n\n// Example usage\ntry {\n    echo changePermissions('chmod', '/path/to/file', '', '755');\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>