class AccountModel extends CI_Model {\n    public function createAccount($accountData, $userData) {\n        $this->db->trans_start();\n\n        // Insert account data\n        $this->db->insert('accounts', $accountData);\n        $accountId = $this->db->insert_id();\n\n        // Insert user data with the created account ID\n        $userData['account_id'] = $accountId;\n        $this->db->insert('users', $userData);\n\n        $this->db->trans_complete();\n\n        return $this->db->trans_status();\n    }\n    \n    // Other account-related methods...\n}