class SignupController extends CI_Controller {\n    public function create() {\n        $this->load->model('AccountModel');\n\n        // Collect account and user data from the request\n        $accountData = [\n            'name' => $this->input->post('account_name'),\n            'email' => $this->input->post('account_email'),\n            // Other account fields...\n        ];\n\n        $userData = [\n            'username' => $this->input->post('username'),\n            'password' => password_hash($this->input->post('password'), PASSWORD_BCRYPT),\n            // Other user fields...\n        ];\n\n        // Call the model method to create account and user\n        if ($this->AccountModel->createAccount($accountData, $userData)) {\n            // Success, redirect or load success view\n            redirect('signup/success');\n        } else {\n            // Failure, show error message\n            $this->load->view('signup/error');\n        }\n    }\n}