<?php\n// script.php\n\n// Check if the form submission is via POST\nif ($_SERVER['REQUEST_METHOD'] === 'POST') {\n    // Get all POST data\n    $inputData = $_POST;\n    \n    // Iterate through each input\n    foreach ($inputData as $inputName => $inputValue) {\n        // Save or process each input as needed\n        // For example, you can save these values to a database or print them\n        echo "Input Name/ID: " . htmlspecialchars($inputName) . " - Value: " . htmlspecialchars($inputValue) . "<br>";\n    }\n}\n?>