// From inside a test...\n$mock = $this->getMockBuilder('myObject')\n             ->setMethods(['methodToMock'])\n             ->getMock();\n\n$mock->expects($this->any())\n     ->method('methodToMock')\n     ->will($this->returnCallback(function($arg) {\n         if ($arg === 'one') {\n             return 'method called with argument "one"';\n         } elseif ($arg === 'two') {\n             return 'method called with argument "two"';\n         } else {\n             return 'method called with unknown argument';\n         }\n     }));\n\n// Example usage\necho $mock->methodToMock('one'); // Outputs: method called with argument "one"\necho $mock->methodToMock('two'); // Outputs: method called with argument "two"\necho $mock->methodToMock('three'); // Outputs: method called with unknown argument