<?php\n// Database connection\n$mysqli = new mysqli("localhost", "username", "password", "database");\n\n// Check connection\nif ($mysqli->connect_error) {\n    die("Connection failed: " . $mysqli->connect_error);\n}\n\n// Assume volunteer_id is provided via GET parameter\n$volunteer_id = intval($_GET['id']);\n\n// Fetch assigned venue for the volunteer\n$volunteer_query = $mysqli->prepare("SELECT venue_id FROM volunteers_2009 WHERE id = ?");\n$volunteer_query->bind_param("i", $volunteer_id);\n$volunteer_query->execute();\n$volunteer_result = $volunteer_query->get_result();\n$volunteer = $volunteer_result->fetch_assoc();\n$assigned_venue_id = $volunteer['venue_id'];\n\n// Fetch all venues\n$venues_query = "SELECT id, venue_name FROM venues ORDER BY venue_name";\n$venues_result = $mysqli->query($venues_query);\n\n// Generate the HTML for the select drop down\necho '<form action="upd.php?id=' . htmlspecialchars($volunteer_id) . '" method="post">';\necho '<select name="venue_id">';\n\nwhile ($row = $venues_result->fetch_assoc()) {\n    $selected = ($row['id'] == $assigned_venue_id) ? ' selected' : '';\n    echo '<option value="' . htmlspecialchars($row['id']) . '"' . $selected . '>' . htmlspecialchars($row['venue_name']) . '</option>';\n}\n\necho '</select>';\necho '<input type="submit" value="submit" name="submit">';\necho '</form>';\n\n// Close the connection\n$mysqli->close();\n?>