<?php\n// Function to resize an image\nfunction resizeImage($file, $width, $height) {\n    list($originalWidth, $originalHeight) = getimagesize($file);\n    $src = imagecreatefromjpeg($file);\n    $dst = imagecreatetruecolor($width, $height);\n    imagecopyresampled($dst, $src, 0, 0, 0, 0, $width, $height, $originalWidth, $originalHeight);\n    return $dst;\n}\n\n// Path to the images folder\n$folderPath = 'path/to/your/images/folder';\n\n// Get all image files from the folder\n$images = glob($folderPath . '/*.jpg'); // You can change the file extension if needed\n\nforeach ($images as $image) {\n    // Resize image to thumbnail size (e.g., 150x150)\n    $thumbnail = resizeImage($image, 150, 150);\n\n    // Save the thumbnail to a temporary file\n    $thumbnailPath = $folderPath . '/thumbnails/' . basename($image);\n    imagejpeg($thumbnail, $thumbnailPath);\n\n    // Display the thumbnail\n    echo '<img src="' . $thumbnailPath . '" alt="' . basename($image) . '">';\n    \n    // Free up memory\n    imagedestroy($thumbnail);\n    imagedestroy(imagecreatefromjpeg($image));\n}\n?>