<?php\n$data = array(\n    array('Area1', null, null),\n    array(null, 'Section1', null),\n    array(null, null, 'Location1'),\n    array('Area2', null, null),\n    array(null, 'Section2', null),\n    array(null, null, 'Location2')\n);\n$root = array();\n$currentArea = null;\n$currentSection = null;\n\nforeach ($data as $row) {\n    if ($row[0]) {\n        $currentArea = &$root[$row[0]];\n        $currentArea = array();\n    } elseif ($row[1]) {\n        $currentSection = &$currentArea[$row[1]];\n        $currentSection = array();\n    } elseif ($row[2]) {\n        $currentSection[] = $row[2];\n    }\n}\nprint_r($root);\n?>