function uncompress($srcName, $dstName) {\n    $bufferSize = 4096; // 4KB chunks\n    $srcFile = gzopen($srcName, 'rb');\n    $dstFile = fopen($dstName, 'wb');\n\n    if ($srcFile === false || $dstFile === false) {\n        return false;\n    }\n\n    while (!gzeof($srcFile)) {\n        $chunk = gzread($srcFile, $bufferSize);\n        fwrite($dstFile, $chunk);\n    }\n\n    gzclose($srcFile);\n    fclose($dstFile);\n\n    return true;\n}