public function search($query)\n  {\n      $params = [\n          'index' => 'your_index_name',\n          'body'  => [\n              'query' => [\n                  'multi_match' => [\n                      'query'  => $query,\n                      'fields' => ['title', 'description', 'comments', 'tags']\n                  ]\n              ]\n          ]\n      ];\n\n      $results = $this->elasticsearchClient->search($params);\n\n      // Process and return results\n      return $results;\n  }