function get_distinct_size_for_bracelets($myvalue) {\n    // Ensure you have already established a PDO connection, for example:\n    // $pdo = new PDO('mysql:host=your_host;dbname=your_db', 'username', 'password');\n\n    $sql = "SELECT DISTINCT size FROM mytable WHERE id = 27 AND type = :type ORDER BY size";\n    $stmt = $pdo->prepare($sql);\n    $stmt->bindParam(':type', $myvalue, PDO::PARAM_STR);\n    $stmt->execute();\n    \n    // Fetch all results\n    $results = $stmt->fetchAll(PDO::FETCH_ASSOC);\n    return $results;\n}