// In your controller\npublic function uploadAction()\n{\n    $form = new Application_Form_Upload();\n    if ($this->getRequest()->isPost()) {\n        if ($form->isValid($this->getRequest()->getPost())) {\n            $adapter = new Zend_File_Transfer_Adapter_Http();\n            $adapter->setDestination('/path/to/upload/directory');\n\n            if ($adapter->receive()) {\n                // File upload was successful\n                $this->view->message = 'File uploaded successfully';\n            } else {\n                // Handle upload error\n                $this->view->message = 'File upload failed';\n            }\n        }\n    }\n\n    $this->view->form = $form;\n}\n\n// In your form class\nclass Application_Form_Upload extends Zend_Form\n{\n    public function init()\n    {\n        $this->setAttrib('enctype', 'multipart/form-data');\n\n        $file = new Zend_Form_Element_File('file');\n        $file->setLabel('Upload a file:')\n             ->setDestination('/path/to/upload/directory')\n             ->setRequired(true)\n             ->addValidator('Size', false, 20971520); // 20MB limit\n\n        $submit = new Zend_Form_Element_Submit('submit');\n        $submit->setLabel('Upload');\n\n        $this->addElements(array($file, $submit));\n    }\n}