<?php\n\n// Define constants\ndefine('MENU_DEFAULT_VALUE', 'default_menu_value');\ndefine('ODP_DEFAULT_VALUE', 'default_odp_value');\n\n// Function with default values as constants\nfunction ftw($foo = 'pwnage', $nub = MENU_DEFAULT_VALUE, $odp = ODP_DEFAULT_VALUE) {\n    // lots_of_awesome_code\n    echo "foo: $foo, nub: $nub, odp: $odp";\n}\n\n// Calling the function\nftw();\n\n?>