class News_model extends CI_Model {\n    \n    public function __construct() {\n        $this->load->database();\n    }\n\n    // Method to insert data into news table and log to audit table\n    public function insert_news($data) {\n        $this->db->insert('news', $data);\n        $news_id = $this->db->insert_id();\n        $this->log_audit($news_id, $data, 'INSERT');\n    }\n\n    // Method to update data and log to audit table\n    public function update_news($id, $data) {\n        $old_data = $this->db->get_where('news', ['id' => $id])->row_array();\n        $this->db->where('id', $id);\n        $this->db->update('news', $data);\n        $this->log_audit($id, $old_data, 'UPDATE');\n    }\n\n    // Method to delete data and log to audit table\n    public function delete_news($id) {\n        $old_data = $this->db->get_where('news', ['id' => $id])->row_array();\n        $this->db->where('id', $id);\n        $this->db->delete('news');\n        $this->log_audit($id, $old_data, 'DELETE');\n    }\n\n    // Helper method to log changes to the audit table\n    private function log_audit($news_id, $data, $operation) {\n        $audit_data = [\n            'news_id' => $news_id,\n            'title' => $data['title'],\n            'content' => $data['content'],\n            'operation' => $operation\n        ];\n        $this->db->insert('news_audit', $audit_data);\n    }\n}