<?php\nfunction getFirstAndLastDate($year, $month) {\n    // Ensure month is two digits\n    $month = str_pad($month, 2, "0", STR_PAD_LEFT);\n\n    // Create DateTime objects for the first and last day of the month\n    $firstDate = new DateTime("$year-$month-01");\n    $lastDate = new DateTime($firstDate->format('Y-m-t'));\n\n    // Format the dates to YYYY-MM-DD\n    $firstDateFormatted = $firstDate->format('Y-m-d');\n    $lastDateFormatted = $lastDate->format('Y-m-d');\n\n    return [\n        'firstDate' => $firstDateFormatted,\n        'lastDate' => $lastDateFormatted\n    ];\n}\n\n// Example usage\n$year = 2023;\n$month = 5;\n$dates = getFirstAndLastDate($year, $month);\necho "First Date: " . $dates['firstDate'] . "\n";\necho "Last Date: " . $dates['lastDate'] . "\n";\n?>