use PHPUnit\Framework\TestCase;\n\nclass FileTest extends TestCase\n{\n    public function testLoadFile()\n    {\n        // Expect that a warning will be triggered\n        $this->expectWarning();\n        $this->expectWarningMessage('file /some/non-existent/file does not exist');\n\n        // Call the method and assert the return value\n        $result = load_file('/some/non-existent/file');\n        $this->assertFalse($result);\n    }\n}