<?php\nsession_start();\n\n// Initialize the current date if not already set\nif (!isset($_SESSION['current_date'])) {\n    $_SESSION['current_date'] = date("Y-m-d");\n}\n\n// Handle the button click\nif (isset($_GET['action'])) {\n    $current_date = $_SESSION['current_date'];\n    \n    if ($_GET['action'] == 'prev') {\n        $_SESSION['current_date'] = date("Y-m-d", strtotime("$current_date -1 day"));\n    } elseif ($_GET['action'] == 'next') {\n        $_SESSION['current_date'] = date("Y-m-d", strtotime("$current_date +1 day"));\n    }\n}\n\n// Display the current date\necho "Current Date: " . $_SESSION['current_date'];\n?>\n\n<!-- Buttons to navigate dates -->\n<p>\n    <a href="?action=prev">-1 day</a>\n    <a href="?action=next">+1 day</a>\n</p>