<?php\n// Define your offer names in an associative array\n$offers = array(\n    1 => "Offer 1 Name",\n    2 => "Offer 2 Name",\n    3 => "Offer 3 Name",\n    // Add more offers as needed\n);\n\n// Fetch the 'id' from the GET parameters and validate it\n$id = isset($_GET['id']) ? intval($_GET['id']) : 0;\n\n// Check if the id exists in the offers array\nif (array_key_exists($id, $offers)) {\n    $offername = $offers[$id];\n} else {\n    $offername = "Default Offer Name"; // Provide a default name or handle the error as needed\n}\n\n// Now you can use $offername in your script\necho $offername;\n?>