<?php\nrequire 'vendor/autoload.php'; // Adjust path to your vendor directory\n\nuse Zend\Date\Date as Zend_Date;\n\nfunction convertToDateUsingZend($input, $format = 'mm-dd-yyyy') {\n    if ($format == 'mm-dd-yyyy') {\n        $date = new Zend_Date($input, 'MM-dd-yyyy');\n    } else if ($format == 'dd-mm-yyyy') {\n        $date = new Zend_Date($input, 'dd-MM-yyyy');\n    } else {\n        throw new Exception('Unsupported date format');\n    }\n\n    return $date->toString('yyyyMMdd');\n}\n\n// Example usage:\ntry {\n    $date1 = convertToDateUsingZend('12-31-2021', 'mm-dd-yyyy');\n    echo $date1; // Outputs: 20211231\n\n    $date2 = convertToDateUsingZend('31-12-2021', 'dd-mm-yyyy');\n    echo $date2; // Outputs: 20211231\n} catch (Exception $e) {\n    echo 'Error: ' . $e->getMessage();\n}\n?>