$title = $fetch_content['article_name'];\n    $keywords = preg_split('/[\s,]+/', $title);\n\n    $placeholders = implode(' OR ', array_fill(0, count($keywords), 'article_name LIKE ?'));\n    $sql_query = "SELECT * FROM tbl_content WHERE status='t' AND ($placeholders) ORDER BY id DESC LIMIT 5";\n\n    $stmt = $connection->prepare($sql_query);\n\n    $keyword_like = array_map(function($keyword) {\n        return '%' . $keyword . '%';\n    }, $keywords);\n\n    $stmt->bind_param(str_repeat('s', count($keywords)), ...$keyword_like);\n    $stmt->execute();\n    $result = $stmt->get_result();\n\n    while ($row = $result->fetch_assoc()) {\n        echo $row['article_name'] . "<br>";\n    }\n\n    $stmt->close();