$title = $fetch_content['article_name']; // Example output: "Hello World, Ask A Question"\n\n// Split the title into individual keywords\n$keywords = preg_split('/[\s,]+/', $title); // Split by spaces and commas\n\n// Construct the SQL query\n$keyword_conditions = array_map(function($keyword) {\n    return "article_name LIKE '%" . addslashes($keyword) . "%'";\n}, $keywords);\n\n$sql_conditions = implode(' OR ', $keyword_conditions);\n$sql_query = "SELECT * FROM tbl_content WHERE status='t' AND ($sql_conditions) ORDER BY id DESC LIMIT 5";\n\n// Execute the query\n$result = mysqli_query($connection, $sql_query);\n\nwhile ($row = mysqli_fetch_assoc($result)) {\n    // Process each related article\n    echo $row['article_name'] . "<br>";\n}