<?php\n// IMAP server details\n$hostname = '{imap.example.com:993/imap/ssl}INBOX';\n$username = 'your-email@example.com';\n$password = 'your-email-password';\n\n// Open an IMAP stream to the mailbox\n$inbox = imap_open($hostname, $username, $password) or die('Cannot connect to IMAP server: ' . imap_last_error());\n\n// Get the number of emails in the inbox\n$numMessages = imap_num_msg($inbox);\n\nif ($numMessages > 0) {\n    // Fetch the most recent email\n    $emailNumber = $numMessages;\n    $overview = imap_fetch_overview($inbox, $emailNumber, 0);\n    $message = imap_fetchbody($inbox, $emailNumber, 1);\n\n    // Print the body of the most recent email\n    echo "Subject: " . $overview[0]->subject . "\n";\n    echo "Body: " . $message;\n} else {\n    echo "No emails found.";\n}\n\n// Close the IMAP stream\nimap_close($inbox);\n?>