<?php\n// Connecting to the IMAP server\n$hostname = '{imap.example.com:993/imap/ssl}INBOX';\n$username = 'your-email@example.com';\n$password = 'your-password';\n\n// Open an IMAP stream to our mailbox\n$conn = imap_open($hostname, $username, $password) or die('Cannot connect to IMAP server: ' . imap_last_error());\n\n// Get the number of messages in the mailbox\n$numMessages = imap_num_msg($conn);\n\n// Fetch the body of the most recent email\n$latest = $numMessages;\n$body = imap_fetchbody($conn, $latest, "1");\n\n// Convert plain text newlines to HTML line breaks\n$body = nl2br($body);\n\n// Output the email body\necho $body;\n\n// Close the IMAP stream\nimap_close($conn);\n?>